polygon_slide:
const pal = palette(blue, purple, [3, 8])
const shapes = [ [3, 'Triangle'], [4, 'Square'], [5, 'Pentagon'], [6, 'Hexagon'], [7, 'Heptagon'], [8, 'Octagon'] ]

const RegularPolygon = ({ n, color }) => {
  const tvals = linspace(0, 2*pi, n+1)
  const fx = t => cos(t - pi/2)
  const fy = t => sin(t - pi/2)
  return <SymPoly aspect fx={fx} fy={fy} tvals={tvals} xlim={[-1, 1]} ylim={[-1, 1]} fill={color} />
}

return <Slide title="Simple Regular Polygons" wrap={25}>
  <Text>A regular polygon has equal side lengths and equal interior angles. Below are examples for <Latex>{"n \\in \\{3, \\ldots, 8\\}"}</Latex></Text>
  <Grid rows={2} spacing={[0.05, 0.075]}>
    { shapes.map(([n, s]) =>
      <Frame rounded fill padding aspect>
        <VStack spacing>
          <RegularPolygon stack-size={0.8} n={n} color={pal(n)} />
          <Text stack-size={0.2}>{`${s} (${n})`}</Text>
        </VStack>
      </Frame>
    ) }
  </Grid>
</Slide>

slick_bars:
const [labs, vals] = zip(
  ["GPT-4o", 0.4], ["OpenAI o1", 3.2], ["OpenAI o4-mini", 8.3], ["Gemini 3 Pro", 12.4],
  ["OpenAI o3", 14.1], ["Grok 4", 15.9], ["Claude Opus 4.5", 17.5], ["GPT-5.2", 25.2],
)
return <Plot
  aspect={1.6} margin={[0.2, 0.1, 0.2, 0.35]} ylim={[0, 30]} xanchor={-0.6} yanchor={-0.55}
  xticks={enumerate(labs)} yticks={linspace(0, 30, 7)} axis-tick-side="outer" xaxis-line-lim={[0, 7]}
  xaxis-label-spin={-45} xaxis-label-justify="right" xaxis-label-loc={0.7}
>
  <Bars rounded={0.1} width={0.85}>{vals}</Bars>
  {vals.map((s, i) => <TextSpan pos={[i, s+1.3]} yrad={0.75}>{`${s}%`}</TextSpan> )}
</Plot>
